sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;

   }


var ga = new GlideAjax('AjaxIncludes');
ga.addParam('sysparm_name','ajaxRecord');
ga.addParam('sysparm_user_name',"Bob");
ga.addParam('sysparm_new',newValue);	
ga.getXML(myCallBack); 

	function myCallBack(response) {
		var a_reply = response.responseXML.documentElement.getAttribute('answer');
		//var b_reply = response.responseXML.documentElement.getAttribute('s_desc');


		alert(a_reply);

	g_form.setDisplayValue('assigned_to',newValue);
	g_form.setDisplayValue('description',a_reply);

	}




}
